mapper.insert:
	INSERT INTO TB_BOARD 
	           (board_no
            , user_id
            , board_category
            , board_title
            , board_content
            , board_date
	            )
	    VALUES (TB_BOARD_NO_SEQ.NEXTVAL
            , #{user_id}
            , #{board_category}
            , #{board_title}
            , #{board_content}
            , TO_CHAR(sysdate, 'YYYY-MM-DD HH:MM:SS')
	          )

INSERT INTO TB_B_FILE
						(file_no
					 , board_no
					 , file_name
    			 , file_url
    			 , file_size
    			 )
    VALUES (TB_B_FILE_NO_SEQ.NEXTVAL
		    	 , #{board_no}
					 , #{file_name}
					 , #{file_url}
					 , #{file_size}
					 )

update.foreach:
UPDATE TB_B_FILE
	SET board_no = #{item.board_no}
WHERE file_name = LTRIM(#{item.file_name})

mapper.update:
UPDATE TB_BOARD


    , board_hit = board_hit + 1


    , board_category = #{board_category}


    , board_title = #{board_title}


    , board_content = #{board_content}


WHERE board_no = #{board_no}

mapper.select:
SELECT u.user_nickname , c.comment_no , c.comment_step , c.comment_content , c.comment_date , c.type_comment , c.comment_status , l.like_count FROM TB_B_COMMENT c , TB_USER u , (SELECT COUNT(user_Id) like_count, like_step, like_group FROM tb_like WHERE like_type = 1 GROUP BY like_step, like_group) l WHERE c.user_id = u.user_id AND c.comment_no = l.like_group(+) AND c.comment_step = l.like_step(+) AND c.board_no = #{board_no}
AND c.comment_no = #{judge}
AND (c.comment_status = 0 OR c.comment_status = 1) ORDER BY c.comment_no ASC, c.comment_step ASC

mapper.insert:
INSERT INTO TB_B_COMMENT 
	           (board_no
            , user_id
            , comment_no

            , comment_step

            , comment_content
            , comment_date
	          )
	    VALUES (#{board_no}
            , #{user_id}

		    		, TB_B_COMMENT_NO_SEQ.NEXTVAL


		    		, #{comment_no}


            , TB_B_COMMENT_STEP_SEQ.NEXTVAL

            , #{comment_content}
            , TO_CHAR(sysdate, 'YYYY-MM-DD HH:MM:SS')
	          )

mapper.update:
UPDATE TB_B_COMMENT


	, comment_content = #{comment_content}


	, comment_status = #{comment_status}


WHERE comment_no = #{comment_no}
	AND comment_step = #{comment_step}

mapper.delete:
DELETE FROM TB_B_COMMENT
WHERE board_no = #{board_no}



		AND comment_no = #{comment_no}



	AND comment_step = #{comment_step}

mapper.insert:
INSERT INTO TB_REPORT
						(report_no
					 , user_id
					 , report_type
					 , report_group

				 	 , report_step

					 , report_user
					 , report_reason
					 , report_date
					 )
		VALUES (TB_REPORT_NO_SEQ.NEXTVAL
					, #{user_id}
					, #{report_type}
					, #{report_group}

					, #{report_step}

					, #{report_user}
					, #{report_reason}
					, TO_CHAR(sysdate, 'YYYY-MM-DD HH:MM:SS')
					 )

INSERT INTO TB_LIKE
						(user_id
					 , like_type
					 , like_no

					 , like_group


				 	 , like_step

					 , like_date
					 )
		 VALUES (#{user_id}
		 			 , #{like_type}
		 			 , #{like_no}

		 			 , #{like_group}


		 			 , #{like_step}

		 			 , TO_CHAR(sysdate, 'YYYY-MM-DD HH:MM:SS')
					 )

mapper.delete:
DELETE FROM TB_LIKE
			WHERE like_no = #{like_no}

				AND user_id = #{user_id}


				AND like_type = #{like_type}


				AND like_group = #{like_group}


				AND like_step = #{like_step}
