mapper.insert:
	INSERT INTO TB_MARKET
	           (market_no
             			,user_id
						 ,market_category  
						 ,market_title     
						 ,market_content  
						 ,market_price    
						 ,market_date 
	            )
	    VALUES (TB_MARKET_NO_SEQ.NEXTVAL
            , #{user_id}
            , #{market_category}
            , #{market_title}
            , #{market_content}
            , #{market_price}
            , TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS')
	          )

INSERT INTO TB_M_FILE
						(file_no
					 , market_no
					 , file_name

    			 , file_url


    			 , file_size

    			 )
    VALUES (TB_M_FILE_NO_SEQ.NEXTVAL
		    	 , #{market_no}
					 , #{file_name}
					 , #{file_url}
					 , #{file_size}
					 )

update.foreach:
UPDATE TB_M_FILE
	SET market_no = #{item.market_no}
WHERE file_name = LTRIM(#{item.file_name})

mapper.update:
UPDATE TB_MARKET


    , market_category = #{market_category}


    , market_title = #{market_title}


    , market_content = #{market_content}


    , market_price = #{market_price}


	, market_like = market_like + 1


	, market_like = market_like - 1


WHERE market_no = #{market_no}
AND user_id=#{user_id}

mapper.insert:
INSERT INTO TB_REPORT
						(report_no
					 , user_id
					 , report_type
					 , report_group
					 , report_user
					 , report_reason
					 , report_date
					 )
		VALUES (TB_REPORT_NO_SEQ.NEXTVAL
					, #{user_id}
					, #{report_type}
					, #{report_group}
					, #{report_user}
					, #{report_reason}
					, TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS')
					 )

INSERT INTO TB_M_REVIEW 
	           (review_no
	          , market_no
            , user_id
            , review_star
           	, review_content
            , review_date
	          )
	    VALUES (TB_M_REVIEW_NO_SEQ.NEXTVAL
	    			, #{market_no}
            , #{user_id}
            , #{review_star}
            , #{review_content}
            , TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS')
	          )

mapper.update:
UPDATE TB_M_REVIEW 


	 review_content = #{review_content}


	, review_star = #{review_star}


WHERE review_no = #{review_no}

mapper.delete:
DELETE FROM TB_M_REVIEW 
WHERE review_no = #{review_no}
	AND market_no = #{market_no}

mapper.insert:
INSERT INTO TB_LIKE
					(user_id
					 , like_type
					 , like_no
					 , like_group
					 ,like_step
					 , like_date
					 )
		 VALUES     (#{user_id}
		 			 , 2
		 			 , #{market_no}
		 			 , #{review_no}
		 			 ,-1
		 			 , TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS')
					 )

mapper.delete:
DELETE FROM TB_LIKE
			WHERE user_id = #{user_id}
				AND like_type = 2
				AND like_group = #{like_group}

DELETE FROM TB_QNA
WHERE qna_no = #{qna_no}
	AND market_no = #{market_no}
